package _01_variable;

public class T03_boolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean bo1 = true;
		boolean bo2 = false;
		System.out.println(bo1);
		
		boolean bo3 = 10 < 3;
		System.out.println(bo3);
		
		int num1 = 10;
		int num2 = 30;
		
		boolean bo4 = num1 < num2;
		System.out.println(bo4);
		
		boolean bo5 = num1 == num2;
		System.out.println(bo5);
		
		
	}

}
